// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.9.0;

contract Bakery {

  // index of created contracts
  address[] public contracts;

  // useful to know the row count in contracts index
  function getContractCount() public view returns(uint contractCount)
  {
    return contracts.length;
  }

function getContractAddress(uint id) public view returns(address contractAddress)
  {
    return contracts[id];
  }

  // deploy a new contract
  function newCookie() public returns (address newContract)
  {
    Cookie c =new Cookie();
    contracts.push(address(c)); //  Need to check if this works!
    return address(c);
  }
}

contract Cookie {

  // suppose the deployed contract has a purpose
  function getFlavor() public pure returns (string memory flavor)
  {
    return "mmm ... chocolate chip";
  }    
}
